package com.ninja.hadoop;import org.apache.hadoop.hbase.util.Bytes;import org.codehaus.jettison.json.JSONException;import org.codehaus.jettison.json.JSONObject;import java.util.ArrayList;import java.util.Iterator;import java.util.List;/** * Created with IntelliJ IDEA. * User: houzhaowei * Date: 10/9/13 * Time: 10:21 PM * To change this template use File | Settings | File Templates. */public class Test {    public int checkCk(String cks1 , String cks2){        JSONObject jsonCk1;        JSONObject jsonCk2;        try {            jsonCk1 = new JSONObject(cks1);            jsonCk2 = new JSONObject(cks2);        } catch (JSONException e) {            return cks1.compareTo(cks2);        }        Iterator<String> iterator1 = jsonCk1.keys();        Iterator<String> iterator2 = jsonCk2.keys();        while (iterator1.hasNext()){            String ck1 = iterator1.next();            while (iterator2.hasNext()){                String ck2 = iterator2.next();                if (ck1 == ck2 || ck1.equals(ck2)){                    String value1;                    String value2;                    try {                        value1 = jsonCk1.getString(ck1);                        value2 = jsonCk2.getString(ck2);                    } catch (JSONException e) {                        continue;                    }                    if(value1 == value2 || value1.equals(value2)){                        System.out.println("same  >>>>>  key = " + ck1 + " value = " + value1);                        return 0;                    }                }            }        }        return cks1.compareTo(cks2);    }    public static void main(String[] args){        byte[] bb = new byte[10];        Bytes.toString(bb);        String ck1 = "{ck1:hello,ck2:world,ck3:fuck,ck4:shit}";        String ck2 = "{ck5:made,ck1:hello}";        System.out.print(ck1.substring(1,ck1.length()-1));    }}